def handle_num(tab) #Compte le nombre d'handle qui contiennent au moins un chiffre

	handle_count = 0
	tab_work = tab
	tab_work.each do |n|
		#print n # Vérification du parsing
		#print " = #{n.size} " #Vérification du comptage
		number_presence = 0

		n.size.times do |i|
			#print ">#{n[i]}" #Vérification du parsing des lettre
			if n[i] == "0" || n[i] == "1" || n[i] == "2" || n[i] == "3" || n[i] == "4" || n[i] == "5" || n[i] == "6" || n[i] == "7" || n[i] == "8" || n[i] == "9"
				number_presence += 1
			end
		end

		if number_presence > 0
			handle_count += 1
		end
	
	end
	return handle_count
end

def letter_parsing (tab) #Parse le tableau en rangeant chaque lettre dans un tableau
	
	tab_work = tab
	tab_single = []
	tab_work.each do |n|
		n.size.times do |i|
			tab_single << n[i]
		end
	end
	#print tab_single
	#print tab_single.size
end

def aude_check (tab) # Vérifie la présence de "aude" dans tous les handle
	
	a = tab.grep(/aude/i).size #L'ajout de im permet de ne pas prendre en compte la CASE des lettres (majuscules et minuscules sont prises en compte)
	return a

end

def underscore_check (tab) #compte le nombre de "_" dans tous les handles
	
	count = 0

	tab_single = []
	tab.each do |n|
		n.size.times do |i|
			tab_single << n[i]
			if n[i] == "_"
				count += 1
			end
		end
	end
	return count
end

def uppercase_after (tab) # retourne le nombre de handle qui commance par une majuscule aprés le @

	count = 0
	b= 0
	tab.each do |n|
		letter_after_at = n.scan(/@./)
		letter_after_at.each do |i|
			b = i.slice!(1)
			check_upper = b.match(/\p{Upper}/)

			if check_upper != nil
				count += 1
			end
		end

	end
	puts count
end

def uppercase_check (tab) #vérifie combien de handle contiennent au moins une majuscule

	count = 0
	tab.each do |n|
		check_upper = n.match(/\p{Upper}/)

		if check_upper != nil
			count += 1
		end

	end
	puts count

end

def sorting (tab) #Tri les handle en retirant le @ puis par ordre alphabétique

	tab.each do |n|
		clean = n.delete! '@'
	end
	tri = tab.sort_by(&:downcase)
	return tri
end

def handle_size (tab) #renvoi la taille de chaque handle dans un hash
	tab_hash = {}
	tab.each do |handle|	
		tab_hash[handle] = handle.size
	end
	return tab_hash
end

def hash_sorting (tab_hash,a)

	#print tab_hash.values.sort #
	tab_hash_sorted = tab_hash.sort_by { |name, length| length } #Tri du hash selon la longueur
	#puts tab_hash
	tab_hash_sorted_a = tab_hash_sorted.first(a).to_h #Nouveau hash des a premiere valeurs demandées dans l'appel de la fonction
	#puts tab_hash_sorted_a.map {|x| x[0]} #.map {|x| x[0]} permet d'afficher les premières valeur du Hash avec 1 on renvoie les deux etc ...
	return tab_hash_sorted_a
	#return tab_hash_sorted_a.map {|x| x[0]}

end

def return_position(tab,a)

	tab.each do |handle|
		if handle == a
			#puts tab.each_with_index.detect { |(value),index| value == a }.last
			return tab.each_with_index.detect { |(value),index| value == a }.last
		end
	end
end


